import React, { FC } from "react";
import { CompResult, SubjectProperty } from "../types";
import FeedbackForm from "./FeedbackForm.tsx";

interface Props {
  subject: SubjectProperty;
  results: CompResult[];
}

const Results: FC<Props> = ({ subject, results }) => {
  return (
    <div style={{ marginTop: "2rem" }}>
      <h2 style={{ borderBottom: "2px solid var(--text-color)", paddingBottom: "0.5rem" }}>
        Top 3 Comparable Properties
      </h2>
      {results.length === 0 ? (
        <p>No results yet.</p>
      ) : (
        <ol style={{ listStyle: "none", paddingLeft: 0 }}>
          {results.map((comp) => (
            <li key={comp.id} style={{
              background: "var(--input-bg)",
              padding: "1rem",
              borderRadius: "8px",
              marginBottom: "1.5rem",
              boxShadow: "0 2px 6px rgba(0, 0, 0, 0.1)"
            }}>
              <strong>{comp.address}</strong> â€” Score: {typeof comp.score === "number" ? comp.score.toFixed(2) : "N/A"}
              {comp.explanation && typeof comp.explanation === "object" && (
                <ul style={{ marginTop: "0.5rem", paddingLeft: "1rem" }}>
                  {Object.entries(comp.explanation).map(([key, val]) => (
                    <li key={key}>
                      <em>{key}</em>: {typeof val === "number" ? val.toFixed(3) : "N/A"}
                    </li>
                  ))}
                </ul>
              )}
              <FeedbackForm subject={subject} comp={comp} />
            </li>
          ))}
        </ol>
      )}
    </div>
  );
};

export default Results;
