import React, { useState, useEffect } from "react";
import { SubjectProperty, CompResult } from "./types";
import SubjectForm from "./components/SubjectForm";
import Results from "./components/Results";
import axios from "axios";
import "./App.css";

const App: React.FC = () => {
  const [subject, setSubject] = useState<SubjectProperty>({
    gla: 0,
    lot_size_sf: 0,
    num_beds: 0,
    num_baths: 0,
    year_built: 0,
    effective_date: "",
    latitude: 0,
    longitude: 0,
    structure_type: "",
    style: "",
    condition: "",
    basement: "",
    address: "",
  });

  const [results, setResults] = useState<CompResult[]>([]);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState<boolean>(() => {
    return localStorage.getItem("theme") === "dark";
  });

  useEffect(() => {
    document.body.className = darkMode ? "dark" : "";
    localStorage.setItem("theme", darkMode ? "dark" : "light");
  }, [darkMode]);

  const handleRecommend = async () => {
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:8000/recommend-from-dataset", {
        address: subject.address,
      });
      setResults(response.data);
    } catch (err) {
      console.error("Recommendation failed", err);
      alert("Could not fetch recommendations. Try using an address from the dataset.");
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <header className="header">
        <h1>üè° Property Comp Recommender</h1>
        <button className="toggle-btn" onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
        </button>
      </header>

      <p className="description">Enter a subject property (must match address from dataset) and we‚Äôll find 3 comparable sales.</p>

      <SubjectForm subject={subject} setSubject={setSubject} />

      <button className="recommend-btn" onClick={handleRecommend} disabled={loading}>
        {loading ? "Evaluating..." : "Get Top 3 Comps"}
      </button>

      <Results subject={subject} results={results} />
    </div>
  );
};

export default App;
