import React, { useState } from "react";
import { Property, SubjectProperty, CompResult } from "./types";
import { getRecommendations } from "./services/api.ts";
import SubjectForm from "./components/SubjectForm.tsx";
import CandidateInput from "./components/CandidateInput.tsx";
import Results from "./components/Results.tsx";

const App: React.FC = () => {
  const [subject, setSubject] = useState<SubjectProperty>({
    effective_date: "",
  });

  const [candidates, setCandidates] = useState<Property[]>([]);
  const [results, setResults] = useState<CompResult[]>([]);
  const [loading, setLoading] = useState(false);

  const handleRecommend = async () => {
    setLoading(true);
    try {
      const recs = await getRecommendations(subject, candidates);
      setResults(recs);
    } catch (err) {
      console.error("Recommendation failed", err);
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "900px", margin: "auto" }}>
      <h1>Property Comp Recommender</h1>
      <SubjectForm subject={subject} setSubject={setSubject} />
      <CandidateInput setCandidates={setCandidates} />
      <button onClick={handleRecommend} disabled={loading}>
        {loading ? "Evaluating..." : "Get Top 3 Comps"}
      </button>
      <Results subject={subject} results={results} />
    </div>
  );
};

export default App;
