import React, { FC } from "react";
import { SubjectProperty } from "../types";

interface Props {
  subject: SubjectProperty;
  setSubject: (value: SubjectProperty) => void;
}

const SubjectForm: FC<Props> = ({ subject, setSubject }) => {
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setSubject({
      ...subject,
      [name]: name.includes("num_") || name.includes("year")
        ? parseFloat(value)
        : value,
    });
  };

  return (
    <div>
      <h3>Subject Property</h3>
      <input type="number" name="gla" placeholder="GLA" onChange={handleChange} />
      <input type="number" name="lot_size_sf" placeholder="Lot Size (sq ft)" onChange={handleChange} />
      <input type="number" name="num_beds" placeholder="Bedrooms" onChange={handleChange} />
      <input type="number" name="num_baths" placeholder="Bathrooms" onChange={handleChange} />
      <input type="number" name="year_built" placeholder="Year Built" onChange={handleChange} />
      <input type="date" name="effective_date" onChange={handleChange} required />
      <input type="number" name="latitude" placeholder="Latitude" onChange={handleChange} />
      <input type="number" name="longitude" placeholder="Longitude" onChange={handleChange} />
      <input type="text" name="structure_type" placeholder="Structure Type" onChange={handleChange} />
      <input type="text" name="style" placeholder="Style" onChange={handleChange} />
      <input type="text" name="condition" placeholder="Condition" onChange={handleChange} />
      <input type="text" name="basement" placeholder="Basement Description" onChange={handleChange} />
    </div>
  );
};

export default SubjectForm;
